import Head from 'next/head'
import Link from 'next/link'
import React from "react";

export default function Home() {
    return (
        <div className="container">
            <Head>
                <title>Daana</title>
                <link rel="icon" href="/favicon.ico"/>
            </Head>

            <main>
                <h1 className="title">
                    Meet <Link href="/posts/first"><a>Daana.</a></Link>

                </h1>

                <p className="description">
                    Are you a <span className={"donor"}>Donor</span> or <span className={"receiver"}>Receiver</span>?
                </p>

                <div className="grid">
                    <a href="https://nextjs.org/docs" className="card">
                        <h3>Donor &rarr;</h3>
                        <p>1. Make Donation to an organization from the list displayed.</p>
                        <p>2. List all the donations made and display it's actual usage.</p>
                        <p>3. Easy pay a certain amount of funds from your account to the selected organisation’s
                            account.</p>
                        <p>4. Visualize the details of all such pools</p>
                    </a>

                    <a href="https://nextjs.org/learn" className="cardDonor">
                        <h3>Receiver &rarr;</h3>
                        <p>1. Initiate a new pool of donation drive.</p>
                        <p>2. Provide a statement for each cent of money it deducts from your account, proved by the
                            consensus system.</p>
                        <p>3. Maintain the details of all such pools</p>
                    </a>

                    <a href="https://nextjs.org/learn" className="cardReceiver">
                        <h3>Receiver &rarr;</h3>
                        <p>1. Initiate a new pool of donation drive.</p>
                        <p>2. Provide a statement for each cent of money it deducts from your account, proved by the
                            consensus system.</p>
                        <p>3. Maintain the details of all such pools</p>
                    </a>

                    <a href="https://nextjs.org/learn" className="cardReceiver">
                        <h3>Receiver &rarr;</h3>
                        <p>1. Initiate a new pool of donation drive.</p>
                        <p>2. Provide a statement for each cent of money it deducts from your account, proved by the
                            consensus system.</p>
                        <p>3. Maintain the details of all such pools</p>
                    </a>
                </div>
            </main>

            <footer>
                <a
                    href="https://vercel.com?utm_source=create-next-app&utm_medium=default-template&utm_campaign=create-next-app"
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    A project for{' '}
                    <img src="/vercel.svg" alt="Vercel Logo" className="logo"/>
                </a>
            </footer>

            <style jsx>{`
        .container {
          min-height: 100vh;
          padding: 0 0.5rem;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        main {
          padding: 5rem 0;
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        footer {
          width: 100%;
          height: 100px;
          border-top: 1px solid #eaeaea;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        footer img {
          margin-left: 0.5rem;
        }

        footer a {
          display: flex;
          justify-content: center;
          align-items: center;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        .title a {
          color: #f36900;
          text-decoration: none;
        }

        .title a:hover,
        .title a:focus,
        .title a:active {
          text-decoration: underline;
        }

        .title {
          margin: 0;
          line-height: 1.15;
          font-size: 4rem;
        }

        .title,
        .description {
          text-align: center;
        }

        .description {
          line-height: 1.5;
          font-size: 1.5rem;
        }

        code {
          background: #fafafa;
          border-radius: 5px;
          padding: 0.75rem;
          font-size: 1.1rem;
          font-family: Menlo, Monaco, Lucida Console, Liberation Mono,
            DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
        }

        .grid {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;

          max-width: 800px;
          margin-top: 3rem;
        }

        .card {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .cardReceiver {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .cardDonor {
          background-color: #29f300
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .card:hover,
        .card:focus,
        .card:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .cardReceiver:hover,
        .cardReceiver:focus,
        .cardReceiver:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .cardDonor:hover,
        .cardDonor:focus,
        .cardDonor:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .card h3, cardReceiver h3, cardDonor h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p, cardReceiver p, cardDonor p {
          margin: 0;
          font-size: 1.25rem;
          line-height: 1.5;
        }

       .donor:hover,
        .donor:focus,
        .donor:active {
          color: #29f300;
          border-color: #29f300;
        }

       .receiver:hover,
        .receiver:focus,
        .receiver:active {
          color: #9600f3;
          border-color: #9600f3;
        }

        .logo {
          height: 1em;
        }

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }
      `}</style>

            <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }

      `}</style>
        </div>
    )
}
